mapper.select:
SELECT p.*, (pl.user_id IS NOT NULL) AS liked_by_current_user
FROM pet_post p
         LEFT JOIN post_like pl ON p.id = pl.post_id AND pl.user_id = #{currentUserId}
WHERE p.id = #{id}

SELECT p.*, u.username AS author_name,
       (pl.user_id IS NOT NULL) AS liked_by_current_user
FROM pet_post p
         LEFT JOIN user u ON p.author_id = u.id
         LEFT JOIN post_like pl ON p.id = pl.post_id AND pl.user_id = #{currentUserId}
WHERE p.id = #{id}

SELECT p.*, u.username AS author_name,
(pl.user_id IS NOT NULL) AS liked_by_current_user
FROM pet_post p
LEFT JOIN user u ON p.author_id = u.id
LEFT JOIN post_like pl ON p.id = pl.post_id AND pl.user_id = #{currentUserId}

     AND p.status = #{status} 
     AND p.category = #{category} 
     AND p.author_id = #{authorId} 
     AND p.title LIKE CONCAT('%', #{title}, '%') 

ORDER BY p.create_time DESC
